import React from 'react';
import Task from './Task';

const Tasks = ({ tasks, taskClick,handleTaskEdit, taskDeletion }) => {
    return (
        <>
            {tasks.map(task =>
                <Task
                    key={ task.id }
                    task={ task }
                    taskClick={taskClick}
                    taskEdit={handleTaskEdit}
                    taskDeletion={ taskDeletion } />)}
        </>
    )
}

export default Tasks;
